package com.huang.leetcode;

// you can also use imports, for example:
// import java.util.*;

// you can write to stdout for debugging purposes, e.g.
// System.out.println("this is a debug message");

class Solution {
    private static final String ONE = "1";
    private static final String ZERO = "0";

    public int solution(String S) {
        // write your code in Java SE 8
        if(S == null || "".equals(S) )
            return 0;

        if(Double.valueOf(S) == 0)
            return 0;

        if(S.length() == 1 && Integer.valueOf(S) == 1)
            return 1;

        if(S.length() > 500) {
            return 5 + solution(S.substring(0, 499)) + solution(S.substring(500, S.length() -1));
        }
        String next = "";

        char lastChar = S.charAt(S.length()-1);
        if (lastChar == 49) {
            StringBuffer buffer = new StringBuffer(S);
            buffer.replace(S.length()-1, S.length(), "0");
            next = buffer.toString();
        }

        if (lastChar == 48) {
            next = S.substring(0, S.length()-1);
        }
        return 1 + solution(next);
    }

    public static void main(String[] args) {
        Solution so = new Solution();
        System.out.println(so.solution(""));
    }
}

